// src/App.jsx
import React, { useState } from 'react'
import TaskList from './components/TaskList'
import TaskAdd from './components/TaskAdd'
import Calendar from './components/Calendar'
import Navigation from './components/Navigation'
import Welcome from './components/Welcome'
import Leaderboard from './components/Leaderboard'
import Login from './components/Login'

import './App.css'
import Signup from './components/Signup'

/**
 * Main App Component
 * Manages the application state and renders the main UI
 */
function App() {
    // State management for tasks and UI
    const [tasks, setTasks] = useState([]) // Stores all tasks
    const [currentView, setCurrentView] = useState('login') // Controls which view is displayed
    const [selectedDate, setSelectedDate] = useState(new Date()) // Selected date for calendar

    //Adds a new task to the tasks array
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState('');


    const addTask = (taskText) => {
        if (taskText.trim()) {
            setTasks([
                ...tasks,
                {
                    id: Date.now(),
                    text: taskText,
                    completed: false,
                    date: selectedDate,
                    points: 10, // Default points value
                },
            ])
        }
    }

    /**
     * Toggles the completion status of a task
     */
    const toggleTask = (id) => {
        setTasks(
            tasks.map((task) =>
                task.id === id ? { ...task, completed: !task.completed } : task
            )
        )
    }


    const handleLoginSuccess = () => {
        setIsLoggedIn(true);
        setCurrentView('welcome'); // Switch to main content on successful login
        return (
        <div>
        <h1>TaskArena</h1>
        <Navigation
            currentView={currentView}
            setCurrentView={setCurrentView}
        />
        <main>
            {currentView === 'welcome' && (
                <Welcome
                    setCurrentView={setCurrentView}
                    username={username}
                />
            )}
            {currentView === 'tasks' && (
                <TaskList
                    tasks={tasks}
                    toggleTask={toggleTask}
                    setCurrentView={setCurrentView}
                />
            )}
            {currentView === 'add' && (
                <TaskAdd
                    addTask={addTask}
                    setCurrentView={setCurrentView}
                />
            )}
            {currentView === 'calendar' && (
                <Calendar
                    selectedDate={selectedDate}
                    setSelectedDate={setSelectedDate}
                />
            )}
            {currentView === 'leaderboard' && <Leaderboard />}
        </main>
    </div>

        )
    };
    function CloseForm() {

        setIsLoggedIn(false);
        setCurrentView('login'); // Switch to main content on successful login
    };
    function handleSignup(){
        setCurrentView('signup');

    }
    return (
        <div className="app">
            {isLoggedIn ? (
                // Main app content after login
                <div>
                    <h1>TaskArena</h1>
                    <Navigation
                        currentView={currentView}
                        setCurrentView={setCurrentView}
                    />
                    <main>
                        {currentView === 'welcome' && (
                            <Welcome
                                setCurrentView={setCurrentView}
                                username={username}
                            />
                        )}
                        {currentView === 'tasks' && (
                            <TaskList
                                tasks={tasks}
                                toggleTask={toggleTask}
                                setCurrentView={setCurrentView}
                            />
                        )}
                        {currentView === 'add' && (
                            <TaskAdd
                                addTask={addTask}
                                setCurrentView={setCurrentView}
                            />
                        )}
                        {currentView === 'calendar' && (
                            <Calendar
                                selectedDate={selectedDate}
                                setSelectedDate={setSelectedDate}
                            />
                        )}
                        {currentView === 'leaderboard' && <Leaderboard />}
                    </main>
                </div>
            ) : (
                // Login/Signup forms
                <>
                    {currentView === 'login' ? (
                        <Login 
                            onLoginSuccess={handleLoginSuccess}
                            PopSignup={handleSignup}
                        />
                    ) : currentView === "signup" ? (
                        <Signup 
                            LoginSuccess={handleLoginSuccess}
                            closeForm={CloseForm}
                        />
                    ) : null}
                </>
            )}

        </div>
    );
}

export default App;
