import mongoose from "mongoose";
import { useState } from "react";
import {Is_User_Name_Exist} from "./Utilities"
import {FetchPostUser} from "./httpUltilities"

function Signup({ closeForm, LoginSuccess }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);




  function CreateAccount(account) {
    Is_User_Name_Exist(username).
      then((exist) => { 
        //If User name already exits alert
        if(exist) {
         //alert("Username already exist");
         setError('Account already exist');
         //setError(null);
        }
        // else if username not exits then create an account on data
        else {
          FetchPostUser(account).
          then((res) => {
            if(res.status === 500) {
              setError("error 500")
              throw error("PostUser error 500");
            }
            else {
              alert("Account created successful");
              LoginSuccess();
            }
          }).
          catch((error) => {
            console.log(error);
          })
        }
      }).
      catch((error) => {console.log("Is_User_Name_Exist() error")});
  }

  function handleSignup(e) {
    e.preventDefault();
    setError(null);
    if (!username || !password) {
      setError('Username and password are required.');
      return;
    }

    else  {
      const account = {"username": username, "password": password}
      //console.log("account= " , account);
      CreateAccount(account);

      /* if(!Is_User_Name_Exist(username)) {
        console.log("creating account");
        postUser(account).
        then((res) => {
        //console.log(res.status);
        if(res.status === 500) {
          throw new Error("error ading user");
        }
        else {
          console.log(res.json());
          return res.json();
        }

        }).then((data) => {

          alert(`Sign up successful`);
        }).catch((error) => {
          console.log("catching error:", error);
        })
      }
      else {
        //alert("Username already exsits");
      } */
  }
}


  return (

    <div className="Signup-Form" id="myForm">

      <h1 className="Signup-header">Sign Up</h1>
      <form className="Signup-form">
        <label className="login-label" ><b>Username</b></label>
        <input type="text" placeholder="Enter Username" name="username" required value={username} onChange={(e) => { setUsername(e.target.value)  }} ></input>

        <label className="login-label" ><b>Password</b></label>
        <input type="password" placeholder="Enter Password" name="password" required value={password} onChange={(e) => { setPassword(e.target.value)}} ></input>
        {error && <p className={`signup-error-message ${error ? 'animate' : ''}`}>{error}</p>}
        <button type="submit" className="login-button" onClick={handleSignup}>Sign up</button>
        <button type="button" className="btn cancel" onClick={closeForm} >Close</button>
      </form>
    </div>
  )

}
export default Signup
